mapper.select:
SELECT INDEX_URL, REQUESTER_PEER_ID, ACTION_TYPE, NOTIFICATION_ADDRESS, STORAGE_SIZE, MAX_UP_BW, MAX_DN_BW, MAX_NUM_CONNECTION, TIMEOUT, MAX_TRAFFIC, NUM_OF_SEEDER, COMPLETE, RESOURCE_ID, VIRTUAL_PEER_ID, RELAY_SESSION_ID, EXPIRES, NETWORK_IP, NETWORK_PORT, LINK, OVERLAY_NETWORK_ID, DATE_FORMAT(CREATED_AT, '%Y-%m-%d %H:%i:%s') CREATED_AT , DATE_FORMAT(UPDATED_AT, '%Y-%m-%d %H:%i:%s') UPDATED_AT FROM uprep_resource_allocation
AND REQUESTER_PEER_ID = #{REQUESTER_PEER_ID}
AND ACTION_TYPE = #{ACTION_TYPE}
AND RESOURCE_ID = #{RESOURCE_ID}
AND VIRTUAL_PEER_ID = #{VIRTUAL_PEER_ID}
AND RELAY_SESSION_ID = #{RELAY_SESSION_ID}
AND INDEX_URL LIKE CONCAT('%',#{INDEX_URL},'%')
AND OVERLAY_NETWORK_ID = #{OVERLAY_NETWORK_ID}

mapper.update:
UPDATE uprep_resource_allocation SET STORAGE_SIZE = #{STORAGE_SIZE}, MAX_UP_BW = #{MAX_UP_BW}, MAX_DN_BW = #{MAX_DN_BW}, MAX_NUM_CONNECTION = #{MAX_NUM_CONNECTION}, TIMEOUT = #{TIMEOUT}, MAX_TRAFFIC = #{MAX_TRAFFIC}, NUM_OF_SEEDER = #{NUM_OF_SEEDER}, COMPLETE = #{COMPLETE}, UPDATED_AT = now()
AND RESOURCE_ID = #{RESOURCE_ID}
AND VIRTUAL_PEER_ID = #{VIRTUAL_PEER_ID}
AND RELAY_SESSION_ID = #{RELAY_SESSION_ID}

mapper.delete:
DELETE FROM 
	uprep_resource_allocation



			AND REQUESTER_PEER_ID = #{REQUESTER_PEER_ID}


			AND RESOURCE_ID = #{RESOURCE_ID}


			AND VIRTUAL_PEER_ID = #{VIRTUAL_PEER_ID}


			AND RELAY_SESSION_ID = #{RELAY_SESSION_ID}


			AND OVERLAY_NETWORK_ID = #{OVERLAY_NETWORK_ID}
