mapper.select:
SELECT OVERLAY_NETWORK_ID, VERSION, OWNER_ID, INDEX_URL, EXPIRES, PAM_ENABLED, PAMS_URL, PAM_INTERVAL, CLOSED, AUTH_KEY, OMS_VP_CNT, DATE_FORMAT(CREATED_AT, '%Y-%m-%d %H:%i:%s') CREATED_AT, DATE_FORMAT(UPDATED_AT, '%Y-%m-%d %H:%i:%s') UPDATED_AT FROM uprep_overlay_network
AND OVERLAY_NETWORK_ID = #{OVERLAY_NETWORK_ID}
AND OWNER_ID = #{OWNER_ID}
AND INDEX_URL LIKE CONCAT('%',#{INDEX_URL},'%')
