mapper.select:
SELECT VIRTUAL_PEER_ID, REQUESTER_PEER_ID, OVERLAY_NETWORK_ID, RESOURCE_ID, INDEX_URL, OMS_ADDRESS, PIECE_SIZE, ACTION_TYPE, STORAGE_SIZE, MAX_UP_BW, MAX_DN_BW, MAX_NUM_CONNECTION, TIMEOUT, MAX_TRAFFIC, NUM_OF_SEEDER, COMPLETED, EXPIRES, NETWORK_IP, NETWORK_PORT, LINK, DATE_FORMAT(CREATED_AT, '%Y-%m-%d %H:%i:%s') CREATED_AT, DATE_FORMAT(UPDATED_AT, '%Y-%m-%d %H:%i:%s') UPDATED_AT FROM uprep_cache_server_reservation
AND VIRTUAL_PEER_ID = #{VIRTUAL_PEER_ID}
AND OVERLAY_NETWORK_ID = #{OVERLAY_NETWORK_ID}
AND REQUESTER_PEER_ID = #{REQUESTER_PEER_ID}
AND RESOURCE_ID = #{RESOURCE_ID}
AND RESOURCE_ID IN (SELECT RESOURCE_ID FROM uprep_cache_server_registration WHERE CS_URL LIKE CONCAT('%',#{CS_URL},'%'))

mapper.delete:
DELETE FROM 
	uprep_cache_server_reservation



			AND VIRTUAL_PEER_ID = #{VIRTUAL_PEER_ID}


			AND OVERLAY_NETWORK_ID = #{OVERLAY_NETWORK_ID}


			AND REQUESTER_PEER_ID = #{REQUESTER_PEER_ID}


			AND RESOURCE_ID = #{RESOURCE_ID}
